mapper.select:
	select *from (select u.*, rownum rn from(
		SELECT * FROM USERS 


			id in

				#{item}



				and id like '%' || #{searchKeyword} || '%'


				and name like '%' || #{searchKeyword} || '%'


		ORDER BY ID                      
    ) u     )a where rn between #{start} and #{end}

mapper.update:
update users set
password = #{password},
name = #{name},
role = #{role}
where id = #[id}
